        $formattedTitle = '';
        $searchTerm = '';
        
        $templateParameters = [
            'itemId' => $id,
            'formattedTitle' => $formattedTitle,
            'searchTerm' => $searchTerm,
            'idPrefix' => $idPrefix,
            'commandName' => $commandName
        ];
        
        return new PlainResponse($this->get('twig')->render('@MUPollsModule/Vote/inlineRedirectHandler.html.twig', $templateParameters));
    }
