namespace MU\PollsModule\Event\Base;

use Symfony\Component\EventDispatcher\Event;
use MU\PollsModule\Entity\PollEntity;

/**
 * Event base class for filtering poll processing.
 */
class AbstractFilterPollEvent extends Event
{
    /**
     * @var PollEntity Reference to treated entity instance.
     */
    protected $poll;

    /**
     * @var array Entity change set for preUpdate events.
     */
    protected $entityChangeSet = [];

    /**
     * FilterPollEvent constructor.
     *
     * @param PollEntity $poll Processed entity
     * @param array $entityChangeSet Change set for preUpdate events
     */
    public function __construct(PollEntity $poll, $entityChangeSet = [])
    {
        $this->poll = $poll;
        $this->entityChangeSet = $entityChangeSet;
    }

    /**
     * Returns the entity.
     *
     * @return PollEntity
     */
    public function getPoll()
    {
        return $this->poll;
    }

    /**
     * Returns the change set.
     *
     * @return array
     */
    public function getEntityChangeSet()
    {
        return $this->entityChangeSet;
    }
}
