/**
 * MUPolls option needle information.
 *
 * @param none
 *
 * @return string with short usage description
 */
function MUPolls_needleapi_option_baseInfo()
{
    $info = array(
        // module name
        'module'  => 'MUPolls',
        // possible needles
        'info'    => 'MUPOLLS{OPTIONS|OPTION-optionId}',
        // whether a reverse lookup is possible, needs MUPolls_needleapi_option_inspect() function
        'inspect' => false
    );

    return $info;
}
